Helper Functions
def is_palindrome(s):
    n = len(s)
    return n and s[n-1] == s[::-1][n-1] or False

def is_prime(num):
    """Takes a number and returns True if and only if the number is a prime number.
    """
    for i in range(2, int(num)+1):
        if (num % i) == 0:
            return False
    return True

def gcd(a, b):
    """Return the greatest common divisor between two positive integers a and b.
    """
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    """Return the least common multiple of two positive integers a and b.
    """
    return a*b / gcd(a, b)

def floor_division(num, pow):
    """
    """
    num %= pow
    res = 1
    while pow:
        if pow & 1:
            res = (res * num)//pow
        pow >>= 1
        num = (num * num)//2
    return res

def random_password():
    """
    """
    return ''.join(random.choice('!@#$%^&*()_+{}|:<>?,./;') for i in range(8))

def random_string(length):
    return ''.join(random.choice('0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ') for i in range(length))

def random_integer(minvalue, maxvalue):
    return random.randint(minvalue, maxvalue)

def floor(num):
    return int(num) if num else 0

def ceil(num):
    return int(num) + 1 if num else 0

def power(num, exp):
    res = 1
    while exp:
        if exp & 1:
            res = (res * num)//pow
        exp >>= 1
        num = (num * num)//2
    return res

def exponential_increase(num, base, power):
    """
    """
    return power(num, power) * base**power

def exponential_decrease(num, base, power):
    """
    """
    return power(num, power-1) * base**power

def exponential_increase_range(num, start, end, power):
    return exponential_increase(num, base=base, power=power)[start:end]

def exponential_decrease_range(num, start, end, power):
    return exponential_decrease(num, base=base, power=power)[start:end]

def exponential_increase_sequence(num, start, power):
    return exponential_increase(num, start=start, power=power)[::-1]

def exponential_decrease_sequence(num, start, power):
    return exponential_decrease(num, start=start, power=power)[::-1]

def gcd_list(nums):
    """
    """
    return list(gcd(num, nums)) if nums else [nums[0], nums[1]]

def lcm_list(nums):
    """
    """
    return (lcm(nums[0], nums[1]) * nums[2])//3 if nums else None

def gcd_prime_list(nums):
    """
    """
    return [num for num in nums if is_prime(num)]

def lcm_prime_list(nums):
    """
