def locate_specialty(patient_info, candidate_specialties):
    if '|' in candidate_specialties:
        specialties = candidate_specialties.split('|')
    else:
        specialties = [candidate_specialties]

    main_condition = patient_info['54']
    sub_condition = patient_info['55']

    for specialty in specialties:
        if main_condition in specialty:
            for i in range(len(specialty)):
                if sub_condition and sub_condition not in specialty[i]:
                    return '{"result": false, "reasons": ["患者不符合该科室的诊疗要求"]}'
            return '{"result": true, "specialties": [%s]}' % specialty[0]
        elif main_condition and main_condition in specialty:
            return '{"result": true, "specialties": [%s]}' % specialty[0]
    return '{"result": false, "reasons": ["未找到与患者主诉和症状相关科室"]}'

