class Solution {
  public List<String> recommend(String patient, List<String> candidateDoctors) {
    if (candidateDoctors.size() < 2) return new ArrayList<>();
    return recommend(patient, candidateDoctors, 0);
  }

  private List<String> recommend(String patient, List<String> candidateDoctors, int depth) {
    if (depth >= candidateDoctors.size()) return new ArrayList<>();
    String firstDoctor = candidateDoctors.get(depth);
    for (int i = depth + 1; i < candidateDoctors.size(); i++) {
      String secondDoctor = candidateDoctors.get(i);
      if (patient.equals(firstDoctor) || patient.equals(secondDoctor) || patient.equals(firstDoctor + " " + secondDoctor))
        return new ArrayList<>(Arrays.asList(firstDoctor, secondDoctor));
      else if (recommend(patient, Arrays.asList(firstDoctor, secondDoctor), i + 1) == null)
        return null;
    }
    return recommend(patient, Arrays.asList(firstDoctor, secondDoctor), depth + 1);
  }
}

