    return vitamin_d_level

def is_safe(self, vitamin_d_level, vitamin_d_level_min):
    """
    :param vitamin_d_level_min: 维生素D水平最小值
    :return:
    """
    if vitamin_d_level_min:
        return (vit_d_level_min and (vit_d_level / vit_d_level_min) < self.vit_d_max)
    return True

