def answer_question(drug_lst, patient_lst):
    for drug in drug_lst:
        if drug.name in patient_lst:
            if drug.is_same_drug(patient_lst[drug.name]):
                return '这些药可以一起吃的。需要餐前吃。'
    return '不建议一起服用。'

def check_question(drug_lst, patient_lst, question_lst):
    for question in question_lst:
        answer = answer_question(drug_lst, patient_lst)
        if answer:
            return answer
        elif question.type == 'input':
            patient_lst[question.name] = question.text
            return answer_question(drug_lst, patient_lst)
        elif question.type == 'button':
            return answer_question(drug_lst, patient_lst)

