public class DataStructures {
    public static class ComparatorByArea implements Comparator<HealthCareAreas> {
        @Override
        public int compare(HealthCareAreas area1, HealthCareAreas area2) {
            return area1.getName().compareTo(area2.getName());
        }
    }
    public static class HealthCareAreas {
        String name;
        int population;
        public HealthCareAreas(String areaName, int areaPopulation) {
            this.name = areaName;
            this.population = areaPopulation;
        }
    }
    public static class ComparatorByHealthcareService implements Comparator<HealthCareService> {
        @Override
        public int compare(HealthCareService service1, HealthCareService service2) {
            return service1.getHealthCareAreas().getPopulation() - service2.getHealthCareAreas().getPopulation();
        }
    }
    public static class HealthCareService {
        LinkedList<HealthCareAreas> healthCareAreas;
        public HealthCareService(LinkedList<HealthCareAreas> healthCareAreas) {
            this.healthCareAreas = healthCareAreas;
        }
    }
