- (void)processInput:(NSData *)inputData {
    dispatch_queue_t que = dispatch_queue_create("com.qmedical.pulse", NULL);
    NSDictionary *dictionary = [NSDictionary dataFromNSData:inputData];
    dispatch_async(que, ^{
        self.inputReader = [[[self inputReader] new] autorelease];
        self.inputReader.data = dictionary;
        self.inputReader.delegate = self;
        dispatch_wait(que, 0);
        NSDictionary *dictionary1 = [self.inputReader data];
        NSDictionary *dictionary2 = [dictionary1 objectForKey:@"MODEL"];
        self.model = [[[self model] new] autorelease];
        self.model.id = [[[[(NSNumber *)[dictionary2 objectForKey:@"PID.id"]] unsignedLongValue]];
        self.model.name = [[[[(NSString *)[dictionary2 objectForKey:@"PID.name"]] autorelease] retain];
        self.model.type = [[[[(NSInteger *)[dictionary2 objectForKey:@"PID.type"]] unsignedLong]];
        self.model.description = [[[[(NSString *)[dictionary2 objectForKey:@"PID.description"]] autorelease] retain];
        self.model.options = self.model.options || [];
        self.model.options.push(self);
        self.model.status = self.model.status || { enabled: true, state: 0 };
        self.model.lastUpdate = [NSDate timeIntervalSinceNow];
        self.model.lastError = [NSNumber numberWithFloat:-1.0];
        self.model.hasError = false;
        self.model.hasInput = true;
        self.model.hasOutput = true;
        self.model.inputCount = self.model.outputCount = [(NSInteger *)[dictionary2 objectForKey:@"PID.count"].unsignedLongValue();
        self.model.inputPorts = self.model.outputPorts = [];
        for (NSInteger i = 0; i < self.model.inputCount; i++) {
            self.model.inputPorts.push(i);
        }
        for (NSInteger i = 0; i < self.model.outputCount; i++) {
            self.model.outputPorts.push(i);
        }
        self.model.isConnected = true;
        self.model.isDirty = true;
        [self addObserver:self forKeyPath:@"MODEL.dirty"];
        dispatch_async(que, ^{
            self.model.status = self.model.status || { enabled: true, state: 0 };
            self.model.hasError = false;
            self.model.hasInput = true;
            self.model.hasOutput = true;
            self.model.inputCount = self.model.outputCount = [(NSInteger *)[dictionary2 objectForKey:@"PID.count"].unsignedLongValue()];
            self.model.inputPorts = self.model.outputPorts = [];
            for (NSInteger i = 0; i < self.model.inputCount; i++) {
                self.model.inputPorts.push(i);
            }
            for (NSInteger i = 0; i < self.model.outputCount; i++) {
                self.model.outputPorts.push(i);
            }
            self.model.isConnected = true;
        });
    });
}

