def has_ancestor(root, node):
    """
    :type root: TreeNode
    :type node: TreeNode
    :rtype: bool
    """
    if (root == node):
        return False
    elif (node.left and has_ancestor(root, node.left)):
        return True
    elif (node.right and has_ancestor(root, node.right)):
        return True
    return has_ancestor(root.left, node), has_ancestor(root.right, node)
